import React from 'react';
import Results from '../Results/Results';
import {getKitInfo, getKitInfoWithID} from '../../api/Api';
import {useEffect, useState} from 'react';

import './Search.css';

const Search = () => {
    const [kitData, setKitData] = useState([]);
    const [idInput, setIdInput] = useState('')

    useEffect(() => {
        getKitInfo().then((data) => {
          setKitData(data);
        })
      }, [])

      const onChangeHandler = (idInput) =>{
        setIdInput(idInput);
      }


    handleSubmit = () => {
        getKitInfo().then((data) => {
            console.log(data);
        });
    }

    return (
        <div className='search-container'>
            <div className='search-box'>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <input className='input search-input' type='text' value={idInput} placeholder="Search Kit ID" onChange={(e)=> onChangeHandler(e.target.value)} />
                    </label>
                    <input className='input input-button' type='submit' value='Submit'/>
                </form>
                <div>{idInput}</div>
            </div>
        </div>
    );
    }

export default Search;
