import React, {FC} from 'react';
import Results from '../Results/Results';
import {getKitInfo, getKitInfoWithID} from '../../api/Api';

import './Search.css';

class Search extends React.Component{

    constructor(props){
        super(props);
        this.kitId = {
            defaultValue: ""};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(kitId) {
        this.kitId.value = kitId;
        console.log(this.kitId);
        getKitInfo().then((data) => {
            console.log(data);
        });
    }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    render(){
    return (
        <div className='search-container'>
            <div className='search-box'>
                <form onSubmit={this.handleSubmit}>
                    <label>
                        <input className='input search-input' type='text' value={this.kitId.value} placeholder="Search Kit ID" onChange={this.handleChange} />
                    </label>
                    <input className='input input-button' type='submit' value='Submit'/>
                </form>
            </div>
        </div>
    );
    }
}

export default Search;
